module.exports={
    addition:function(number1,number2){
        return number1+number2;
    },

    subtraction:function(number1,number2){
        return number1-number2;
    },

    multiplication:function(number1,number2){
        return number1*number2;
    },

    division:function(number1,number2){
        return number1/number2;
    },

    modulus:function(number1,number2){
        return number1%number2;
    }
};